mapper.update:
update attendance_detail
e_id = #{eId,jdbcType=INTEGER}, created_date = #{createdDate,jdbcType=DATE}, arrive_time = #{arriveTime,jdbcType=TIMESTAMP}, left_time = #{leftTime,jdbcType=TIMESTAMP},
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, e_id, created_date, arrive_time, left_time
from attendance_detail
where id = #{id,jdbcType=INTEGER}
