mapper.update:
update department
name = #{name,jdbcType=VARCHAR}, leader_id = #{leaderId,jdbcType=INTEGER}, leader_name = #{leaderName,jdbcType=VARCHAR}, personnel_num = #{personnelNum,jdbcType=INTEGER},
