mapper.update:
update employee
name=#{name,jdbcType=VARCHAR}, title=#{title,jdbcType=VARCHAR}, salary=#{salary,jdbcType=VARCHAR}, sign_date=#{signDate,jdbcType=TIMESTAMP}, department_id = #{departmentId,jdbcType=INTEGER}, image = #{image,jdbcType=LONGVARBINARY},
where id = #{id,jdbcType=INTEGER};

mapper.select:
select id, name, title, salary, sign_date, department_id, image
from employee
where id = #{id,jdbcType=INTEGER}
